function repeatStringNumTimes(str, num) {
  // repeat after me
  let newStr = '';

  // with a 'for' loop
  //   for (i = 0; i < num; i++) {
  //     newStr += str;
  //   }

  // with a 'while' loop
  while (num > 0) {
    newStr += str;
    num--;
  }
  return newStr;
}

repeatStringNumTimes("abc", 3);
