function titleCase(str) {
//   return str;
  let capArr = [];
  let newStr = '';
  let lowerCaseArr = str.toLowerCase().split(' ');

  for (let i = 0; i < lowerCaseArr.length; i++){

    capArr[i] = lowerCaseArr[i].charAt(0).toUpperCase() + lowerCaseArr[i].slice(1).toLowerCase();
  }

  return newStr = capArr.join(' ');

}

